import React from "react";
import millify from "millify";
import { Typography, Row, Col, Statistic } from "antd";
import { Link } from "react-router-dom";

import { useGetCryptosQuery  } from "../services/cryptoApi";

const { Title } = Typography;

const Homepage = () => {
  const { data, isFetching } = useGetCryptosQuery();
  console.log(data);
  const globalStats = data?.data?.stats;

  if (isFetching) return 'Loading ...';
  return (
    <>
      <Title level={2} className="heading">
        Global Crypto Stats
      </Title>
      <Row>
        <Col span={12}>
          <Statistic title="Total Cryptocurrencies" value={globalStats.total}></Statistic>
        </Col>
        <Col span={12}>
          <Statistic title="Total Exchanges" value={globalStats.totalExchanges}></Statistic>
        </Col>
        <Col span={12}>
          <Statistic title="Total Market Cap" value={globalStats.totalMarketCap}></Statistic>
        </Col>
        <Col span={12}>
          <Statistic title="Total 24h Volume" value={globalStats.totalMarketCaap}></Statistic>
        </Col>
        <Col span={12}>
          <Statistic title="Total Markets" value="5"></Statistic>
        </Col>
      </Row>
    </>
  );
};

export default Homepage;
